import TinderCard from "react-tinder-card";
import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import ChatContainer from '../components/ChatContainer'
import axios from "axios";

const Dashboard = () => {
    const [user, setUser] = useState(null)
    const [genderedUsers, setGenderedUsers] = useState(null)
    const [lastDirection, setLastDirection] = useState()

    const [cookies, setCookie, removeCookie] = useCookies(['user'])

    const userId = cookies.UserId

    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                params: { userId }
            })
            setUser(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    const getGenderedUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8000/gendered-users', {
                params: { gender: user?.gender_interest }
            })
            setGenderedUsers(response.data)
        } catch (error) {
            console.log(error)
        }
    }


    useEffect(() => {
        getUser()
        getGenderedUsers()
    }, [])

    console.log(genderedUsers)



    const swiped = (direction, nameToDelete) => {
        console.log('removing' + nameToDelete)
        setLastDirection(direction)
    }

    const outOfFrame = (name) => {
        console.log(name + 'left the screen')
    }

    return (
        <>
            {user &&
                <div className="dashboard">
                    <ChatContainer user={user} />
                    <div className="swipe-container">
                        <div className="card-container">
                            {genderedUsers?.map((genderedUsers) =>
                                <TinderCard
                                    className="swipe"
                                    key={genderedUsers.name}
                                    onSwipe={(dir) => swiped(dir, genderedUsers.name)}
                                    onCardLeftScreen={() => outOfFrame(genderedUsers.name)}>
                                    <div
                                        style={{ backgroundImage: "url(" + genderedUsers.url + ")" }}
                                        className="card">
                                        <h3>{genderedUsers.name}</h3>
                                    </div>
                                </TinderCard>
                            )}
                            <div className="swipe-info">
                                {lastDirection ? <p>You swiped {lastDirection}</p> : <p />}
                            </div>

                        </div>
                    </div>
                </div>}
        </>
    );
}

export default Dashboard;
